import React from 'react'
import { ChakraProvider } from "@chakra-ui/react"
import Welcome from './components/Welcome'
import Session1 from './components/Session1'
import Session5 from './components/Session5'
import Session10 from './components/Session10'
import Landing from './components/Landing'
import GetStarted from './components/GetStarted'
import Zoom from './components/Zoom'
import MyCommunity from './components/MyCommunity'
import ContactPerson from './components/ContactPerson'
import Inspo from './components/Inspo'
import Events from './components/Events'
import NavBar from './components/NavBar'
import Messages from './components/Messages'
import client from "./Client"
import { gql, useQuery } from '@apollo/client'

import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";
  import { useAuth0 } from "@auth0/auth0-react";
  import { ApolloProvider } from '@apollo/client';


  
  
const App = () => {

  const { user, isAuthenticated, isLoading } = useAuth0();

  const UserData= gql`
  query($email: String!){
     tat4_members(where: {member_email: {_eq: $email}}){
        
         member_id
         member_email
         member_first_name
         member_active
         member_next_session_id
     }
 }
 
  `
  const { loading, error, data } = useQuery(UserData, {
    variables: { email: user.email },
  });
  if(loading) {
    return (
        <p>Loading data...</p>
    )
}

    console.log(data)
  if(data.tat4_members[0] === undefined) {
      return (
          <p>
              Sorry, you are not registered please gand mrwao first.
          </p>
      )
  }

  let theId = data.tat4_members[0].member_id;


  return (
        <>
        <ApolloProvider client={client}>
        {isAuthenticated &&(
          <ChakraProvider>
          <Session1 name={data.tat4_members[0].member_first_name}/>
         
          </ChakraProvider>
         
          
        )}
        
       
        <ChakraProvider>
        
            <Router>
              
                <Route path="/" exact={true}>        
                <Welcome/>
                </Route>
        
                <Route path="/welcome" component={Welcome} />
                <Route path="/zoom" component={Zoom} />
                <Route path="/session1" component={Session1} />
                <Route path="/session5" component={Session5} />
                <Route path="/session10" component={Session10} />
                <Route path="/landing" component={Landing} />
                <Route path="/get-started" component={GetStarted} />
                <Route path="/community" component={MyCommunity} />
                <Route path="/contact-person" component={ContactPerson} />
                <Route path="/inspo" component={Inspo} />
                <Route path="/events" component={Events} />
                <Route path="/messages" component={Messages} />
            </Router>
            
            
        </ChakraProvider>,
        </ApolloProvider>
        </>
    )
}

export default App
