import { ApolloClient, InMemoryCache } from '@apollo/client';
import { gql } from '@apollo/client';

const Client = new ApolloClient({
  uri: 'https://rare-robin-15.hasura.app/v1/graphql',
  cache: new InMemoryCache(),
  headers: {
      "X-Hasura-Admin-Secret": "JD3PfJPjnfU7t96"
  }
});

export const GET_MEMBERS = gql`
  query {
    tat4_members {
        member_id    
        member_first_name
        member_email
        member_password
    }

  }
`;


export const GET_SESSIONS = gql`
  query {
    tat4_meeting_sessions {
        meeting_session_id    
        meeting_session_date
        meeting_slot_1_member 
        tat4MemberByMeetingSlot1Member{
            member_first_name
            member_linkedIn_url
        }    
        meeting_slot_2_member
        tat4MemberByMeetingSlot2Member{
            member_first_name
            member_linkedIn_url
        }
        meeting_slot_3_member
        tat4MemberByMeetingSlot3Member{
            member_first_name        
            member_linkedIn_url
        }
       
        meeting_slot_4_member
        Tat4_Member{
            member_first_name        
            member_linkedIn_url
        }

    
        meeting_slot_1_attending
        meeting_slot_2_attending
        meeting_slot_3_attending
        meeting_slot_4_attending
        meeting_link_url
    }

  }
`;


export const GET_NEWS = gql`
  query {
    tat4_news {
        news_item_id
        news_image_item
        news_end_date
        display_news_item
    }
  }
`;



export const getMembers = client.query({
    query: gql`
    query {
        tat4_members {
            member_id    
            member_first_name
            member_email
            member_password
        }
     
      }
    `
  })
  .then(result => {
      return result
    });
export default Client;
